// DATASET: nationalParks from ./datasets/nationalParks

const nationalParksPrompts = {
  getParkVisitList() {
    /// Return an object containing the names of which parks I need to visit
    // and the ones I have already visited eg:
    // {
    //   parksToVisit: ["Yellowstone", "Glacier", "Everglades"],
    //   parksVisited: ["Rocky Mountain", "Acadia", "Zion"]
    //}
    let sortedParks = {
      parksToVisit: [],
      parksVisited: []
    }

    nationalParks.forEach(park => {
      if (!park.visited) {
        sortedParks.parksToVisit.push(park.name);
      } else {
        sortedParks.parksVisited.push(park.name);
      }
    })

    return sortedParks;
    // Annotation:
    // Write your annotation here as a comment
  },

  getParkInEachState() {
    // Return an array of objects where the key is the state and the value is its National Park
    // eg: [ { Colorado: 'Rocky Mountain' },
    // { Wyoming: 'Yellowstone' },
    // { Montana: 'Glacier' },
    // { Maine: 'Acadia' },
    // { Utah: 'Zion' },
    // { Florida: 'Everglades' } ]

    return nationalParks.map(park => {
      let parkLocation = {};
      parkLocation[park.location] = park.name;
      return parkLocation;
    });

    // Annotation:
    // Write your annotation here as a comment
  },

  getParkActivities() {
    // Return an array of all the activities I can do
    // in a National Park. Make sure to exclude duplicates. eg:
    // [ 'hiking',
    //   'shoeshoing',
    //   'camping',
    //   'fishing',
    //   'boating',
    //   'watching wildlife',
    //   'cross-country skiing',
    //   'swimming',
    //   'bird watching',
    //   'canyoneering',
    //   'backpacking',
    //   'rock climbing' ]

    let allActivities = nationalParks.map(park => park.activities)
    .flat()
    
    return [...new Set(allActivities)];

    // Annotation:
    // Write your annotation here as a comment
  }
};



